import React, { useState } from "react";
import { Row, Col, Drawer } from "antd";
import { withTranslation } from "react-i18next";
import Container from "../../common/container";
import {SvgIcon} from "../../common/svgIcon"; // Assuming SvgIcon is a default import
import {Button} from "../../common/button"; // Assuming Button is a default import
// import {
//   HeaderSection,
//   LogoContainer,
//   Burger,
//   NotHidden,
//   Menu,
//   CustomNavLinkSmall,
//   Label,
//   Outline,
//   Span,
// } from "./styles";
import './styles.css'

const Header = ({ t }) => {
  const [visible, setVisibility] = useState(false);

  const toggleButton = () => {
    setVisibility(!visible);
  };

  const MenuItem = () => {
    const scrollTo = (id) => {
      const element = document.getElementById(id);
      element.scrollIntoView({
        behavior: "smooth",
      });
      setVisibility(false);
    };
    return (
      <>
        {/* <CustomNavLinkSmall onClick={() => scrollTo("about")}>
          <Span>{t("Giới Thiệu")}</Span>
        </CustomNavLinkSmall>
        <CustomNavLinkSmall onClick={() => scrollTo("mission")}>
          <Span>{t("Chúng Tôi")}</Span>
        </CustomNavLinkSmall>
        <CustomNavLinkSmall onClick={() => scrollTo("product")}>
          <Span>{t("Dịch Vụ")}</Span>
        </CustomNavLinkSmall>
        <CustomNavLinkSmall onClick={() => scrollTo("contact")}>
          <Span>{t("Sản Phẩm")}</Span>
        </CustomNavLinkSmall>
        <CustomNavLinkSmall style={{ width: "120px" }} onClick={() => scrollTo("contact")}>
          <Span>
            <Button>{t("Đặt lịch")}</Button>
          </Span>
        </CustomNavLinkSmall>
        <CustomNavLinkSmall style={{ width: "120px" }} onClick={() => scrollTo("")}>
          <Span>
            <Button>{t("Đăng nhập")}</Button>
          </Span>
        </CustomNavLinkSmall> */}
        <div className="custom-nav-link-small" onClick={() => scrollTo("about")}>
          <span className="span">{t("Giới Thiệu")}</span>
        </div>
      </>
    );
  };

  return (
    <HeaderSection>
      <Container>
        <Row justify="space-between">
          <LogoContainer to="/" aria-label="homepage">
            <SvgIcon src="logo.png" width="101px" height="25px" />
          </LogoContainer>
          <NotHidden>
            <MenuItem />
          </NotHidden>
          <Burger onClick={toggleButton}>
            <Outline />
          </Burger>
        </Row>
        <Drawer closable={false} open={visible} onClose={toggleButton}>
          <Col style={{ marginBottom: "2.5rem" }}>
            <Label onClick={toggleButton}>
              <Col span={12}>
                <Menu>Menu</Menu>
              </Col>
              <Col span={12}>
                <Outline />
              </Col>
            </Label>
          </Col>
          <MenuItem />
        </Drawer>
      </Container>
    </HeaderSection>
  );
};

export default withTranslation()(Header);
